package week1.day2;

public class LearnIfLadder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int score = 60;
		
		if(score > 90)
		{ // 95> 90
			System.out.println("Play station");
		}
		else if(score > 70) 
		{ // 45>70
			System.out.println("Toys");
		}
		else if(score > 50)
		{ //45 > 50
			System.out.println("Ice cream");
		}
		else {
			System.out.println(":(");
		}
		
	}

}
